import React, { Component, useCallback } from 'react';
import PropTypes from 'prop-types';
// import Modal from '@material-ui/core/Modal';
import Carousel, { Modal, ModalGateway } from "react-images";
import { withStyles, Box } from '@material-ui/core';
import { Dashboard as DashboardLayout } from '../../layouts';
import { images } from './preload-images.json';
import Gallery from 'react-photo-gallery';

const styles = theme => ({
  root: {
    justifyContent: 'space-around',
    zIndex: '200',
  },
  gridList: {
    width: '100%',
    height: '100%',
  },
});

class Dashboard extends Component {
  constructor(props) {
    super(props);
    this.state = {
      currentImage: 0,
      viewerIsOpen: false,
    };
  }

  openLightbox = (event, {photos, index}) => {
    console.log(index);
    console.log("open");
    this.setState({ currentImage: index, viewerIsOpen: true });
  };

  closeLightbox = () => {
    console.log("close");
    this.setState({ currentImage: 0, viewerIsOpen: false});
  };

  render() {
    const { classes } = this.props;
    const { currentImage, viewerIsOpen } = this.state;
    var arr = [];
    images.forEach(function( img ) { arr.push({"src":require(img.src+""),"width":img.width,"height":img.height}) });
    return (
      <>
        <DashboardLayout title="genlouise.art">
            <Box class={classes.root}>
              <Gallery photos={arr} direction='column' columns={3} onClick={this.openLightbox}/>
            </Box>
            {viewerIsOpen ? (
            <Modal onClick={this.closeLightbox}>
              <Carousel
                currentIndex={currentImage}
                views={images.map(x => (
                  {src: x.src}
                ))}
              />
            </Modal>
          ) : console.log("mkay")}
        </DashboardLayout>
      </>
    );
  }
}

Dashboard.propTypes = {
  classes: PropTypes.object.isRequired
};

export default withStyles(styles)(Dashboard);
