import React, { Component, useCallback, useState } from 'react';
import Carousel, { Modal, ModalGateway } from "react-images";
import PropTypes from 'prop-types';
import { withStyles, Box } from '@material-ui/core';
import { Dashboard as DashboardLayout } from '../../layouts';
import { images } from './preload-images.json';
import Gallery from 'react-photo-gallery';

const styles = theme => ({
  root: {
    justifyContent: 'space-around',
    zIndex: '200',
  },
  gridList: {
    width: '100%',
    height: '100%',
  },
});

class Dashboard extends Component {
  render() {
    const { classes } = this.props;
    const [currentImage, setCurrentImage] = useState(0);
    const [viewerIsOpen, setViewerIsOpen] = useState(false);
    var arr = [];
    images.forEach(function( img ) { arr.push({"src":require(img.src+""),"width":img.width,"height":img.height}) });

    const openLightbox = useCallback((event, { photo, index }) => {
      setCurrentImage(index);
      setViewerIsOpen(true);
    }, []);

    const closeLightbox = () => {
      setCurrentImage(0);
      setViewerIsOpen(false);
    };
    document.addEventListener('contextmenu', event => event.preventDefault());
    return (
      <DashboardLayout title="genlouise.art">
          <Box class={classes.root}>
            <Gallery photos={arr} direction='column' columns={3} onClick={openLightbox}/>
            <ModalGateway>
              {viewerIsOpen ? (
                <Modal onClose={closeLightbox}>
                  <Carousel
                    currentIndex={currentImage}
                    views={photos.map(x => ({
                      ...x,
                      srcset: x.srcSet,
                      caption: x.title
                    }))}
                  />
                </Modal>
              ) : null}
            </ModalGateway>
          </Box>
      </DashboardLayout>

    );
  }
}

Dashboard.propTypes = {
  classes: PropTypes.object.isRequired
};

export default withStyles(styles)(Dashboard);
