import React, { Component, useCallback, useState } from 'react';
import Carousel, { Modal, ModalGateway } from "react-images";
import PropTypes from 'prop-types';
import { withStyles, Box } from '@material-ui/core';
import { Dashboard as DashboardLayout } from '../../layouts';
import { images } from './preload-images.json';
import Gallery from 'react-photo-gallery';

const styles = theme => ({
  root: {
    justifyContent: 'space-around',
    zIndex: '200',
  },
  gridList: {
    width: '100%',
    height: '100%',
  },
});

openLightbox = useCallback((event, { photo, index }) => {
  this.setState({ setCurrentImage: index });
  this.setState({ setViewerIsOpen: true} );
}, []);

class Dashboard extends Component {
  constructor(props) {
    super(props);
    this.state = {
      currentImage: 0,
      setCurrentImage: 0,
      viewerIsOpen: false,
      setViewerIsOpen: false,
    };
  }


  closeLightbox = () => {
    this.setCurrentImage(0);
    this.setViewerIsOpen(false);
  };

  render() {
    const { classes } = this.props;
    const { currentImage, setCurrentImage, viewerIsOpen, setViewerIsOpen } = this.state;
    var arr = [];
    images.forEach(function( img ) { arr.push({"src":require(img.src+""),"width":img.width,"height":img.height}) });
    document.addEventListener('contextmenu', event => event.preventDefault());
    return (
      <DashboardLayout title="genlouise.art">
          <Box class={classes.root}>
            <Gallery photos={arr} direction='column' columns={3} onClick={this.openLightbox}/>
            <ModalGateway>
              {viewerIsOpen ? (
                <Modal onClose={this.closeLightbox}>
                  <Carousel
                    currentIndex={currentImage}
                    views={images.map(x => ({
                      ...x,
                      srcset: x.srcSet,
                      caption: x.title
                    }))}
                  />
                </Modal>
              ) : null}
            </ModalGateway>
          </Box>
      </DashboardLayout>

    );
  }
}

Dashboard.propTypes = {
  classes: PropTypes.object.isRequired
};

export default withStyles(styles)(Dashboard);
